pragma solidity 0.8.24;

import {SafeCast} from "@openzeppelin/contracts-v4/utils/math/SafeCast.sol";

import {ZKChainBase} from "./ZKChainBase.sol";
import {PubdataPricingMode} from "../ZKChainStorage.sol";
